import{j as e,P as t,c as a,r as n}from"./PatternDocument-BlLjFXs0.js";const i={title:"Conversational AI Assistant - Example Template",patternId:"00-EXAMPLE",interactionModels:["Conversational"],aiCapabilities:["Informational","Recommendations"],lastUpdated:"December 2025",overview:{images:[{src:"/img/exampletemplate/askdela_overview1.png",caption:"Ask Dela entry point visible in global navigation"},{src:"/img/exampletemplate/askdela_overview2.png",caption:"Conversation interface in context with the main application"}],interactionModel:{label:"Conversational",description:"Users initiate and maintain dialogue through natural language. The system responds in a conversational manner, maintaining context across multiple exchanges.",whyItMatters:"Requires chat interface patterns, turn-taking mechanics, context management, and conversation controls."},aiCapabilities:{label:"Informational + Recommendations",description:"Provides information and guidance in response to queries, and may suggest next steps or actions.",whyItMatters:"Determines response formatting, how suggestions are presented, and what controls users need to act on recommendations."},whatItIs:"A persistent AI assistant that users can interact with through natural language to get information, complete tasks, or receive guidance. The assistant maintains context across a conversation and can handle a range of queries related to the product domain.",primaryUserGoal:"Get answers to questions, navigate the system, or accomplish tasks without knowing exact menu paths or feature locations."},whenToUse:{whenRightFit:{heading:"When Conversational Interaction is the right fit:",bullets:["User needs vary widely and can't be predicted in advance","Questions require clarification or back-and-forth dialogue","Users benefit from natural language rather than structured menus/forms","The AI needs to maintain context across multiple exchanges"]},goodFitCapabilities:[{name:"Informational",description:"Answering questions, explaining features, providing guidance",muted:!1},{name:"Recommendations",description:"Suggesting next steps, recommending features to use",muted:!1},{name:"Generation",description:"Possible but consider on-demand patterns for content generation",muted:!0},{name:"Automation",description:"Possible but consider autonomous agent patterns for task execution",muted:!0}],considerDifferent:{heading:"Consider different interaction models when:",bullets:[{text:"Ambient/Contextual",detail:"if AI should proactively surface information without asking"},{text:"On-demand",detail:"if user knows exactly what AI action they want to trigger"},{text:"Autonomous",detail:"if AI should work in background without real-time interaction"}]}},components:{images:[{src:"/img/exampletemplate/askdela_core1.png",caption:"Core pattern components: entry point, conversation panel, and response patterns"}],items:[{name:"Entry Point",subtitle:"How users access the assistant",details:[{label:"Standard implementation",value:"Persistent icon/button in global navigation (typically bottom-right)"},{label:"Visual treatment",value:"Branded icon with subtle animation/pulse to indicate availability"}]},{name:"Conversation Interface",subtitle:"The chat window where interaction happens",details:[{label:"Layout",value:"Overlay panel or modal, typically 400-500px wide"},{label:"Input",value:"Text field with send button, support for multi-line input"},{label:"Message display",value:"User messages right-aligned, AI responses left-aligned"},{label:"Conversation history",value:"Scrollable, maintains context within session"}]},{name:"AI Response Patterns",subtitle:"How the assistant communicates back",details:[{label:"Text responses",value:"Clear, concise answers in conversational tone"},{label:"Rich content",value:"Links, buttons, or cards when directing to features"},{label:"Loading states",value:"Typing indicator while processing"},{label:"Citations",value:"When pulling from documentation or data, show source"}]},{name:"Transparency & Control",subtitle:"Building user trust and understanding",details:[{label:"Limitations disclosure",value:"Clear about what it can/cannot do"},{label:"Suggested prompts",value:"Help users understand capabilities"},{label:"Feedback mechanism",value:"Thumbs up/down on responses"},{label:"Conversation controls",value:"Clear/reset conversation, dismiss"}]}]},examples:{images:[{src:"/img/exampletemplate/askdela_examples1.png",caption:"Ask Dela implemented in Costpoint"},{src:"/img/exampletemplate/askdela_examples2.png",caption:"Ask Dela in another product context"}],items:[{name:"Ask Dela",badge:"All Products",description:"Company-wide AI assistant available across all Deltek products. Answers questions about product features, helps with navigation, and provides contextual guidance.",keyFeatures:"Persistent across sessions, product-aware, can deep-link to features"}],designerNote:"When implementing this pattern in your product, ensure Ask Dela branding and interaction model remain consistent. Product-specific customization should be limited to the knowledge base and feature linking."},variations:{tableHeaders:["Component","Standard","When Deviation Acceptable"],rows:[["Entry point location","Bottom-right persistent button","Framework constraints prevent fixed positioning; technical limitations with mobile layouts"],["Conversation panel size","400-500px width","Mobile/tablet adaptations; integration with existing panel systems"],["Response format","Text + rich content cards","Product-specific data visualization needs; technical capabilities of framework"]]},considerations:{usability:[{label:"Context awareness",text:"Should the assistant know where the user is in the product? This improves relevance but increases complexity."},{label:"Conversation persistence",text:"Should history persist across sessions? Better UX but potential privacy concerns."},{label:"Scope boundaries",text:"Clearly communicate what the assistant can/cannot do to prevent user frustration."}],technical:["Response time expectations (aim for under 3 seconds)","Integration with product-specific data and permissions","Fallback behavior when AI service is unavailable","Framework limitations on positioning and layout"],commonPitfalls:["Overestimating AI capabilities leads to disappointed users","Poor error handling when AI misunderstands or fails","Inconsistent tone across different types of responses","Lack of escape routes when conversation isn't helping"]},metadata:{patternOwner:"Charlie",nextReview:"January 2026"}},s=()=>e.jsx(t,{data:i,templateMode:!0});a.createRoot(document.getElementById("root")).render(e.jsx(n.StrictMode,{children:e.jsx("div",{className:"min-h-screen bg-gray-100 py-8",children:e.jsx(s,{})})}));
